package com.ex;

public class Operator04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 비교연산자 
		// < > <= >= == !=
		int a = 10, b = 20;
		System.out.println("a<b :" + (a<b));
        System.out.println("a>b :" + (a>b));
        System.out.println("a ==b : " + (a==b));
        System.out.println("a!=b :" + (a!=b));
	}

}
