package com.ex;

public class Operator06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub 
		// 삼항연산자 
		// 형태 : 조건식 ? 참일 경우 출력할 내용 : 거짓일 경우 출력할 내용
		
		int a = 10, b = 20;
		String c = (a > b) ? "참입니다." : "거짓입니다";
		System.out.println(c);
		
	  // 진법변환 프로그램 만들기

	}

}
